@param \\CYA\\YogaBundle\\Entity\\Movimiento $movimiento\n     */\n    public function removeMovimiento(\\CYA\\YogaBundle\\Entity\\Movimiento $movimiento)\n    {\n        $this->movimientos->removeElement($movimiento);\n    }\n\n    /**\n     * Get movimientos\n     *\n     * @return \\Doctrine\\Common\\Collections\\Collection\n     */\n    public function getMovimientos()\n    {\n        return $this->movimientos;\n    }\n\n    public function getUsername()\n    {\n        return $this->nombreusuario;\n    }\n    \n    public function getRoles()\n    {\n        return array($this->rol);\n    }\n    \n    public function getSalt()\n    {\n        return null;\n    }\n    \n    public function eraseCredentials()\n    {\n\n    }\n    \n    /** @see \\Serializable::serialize() */\n    public function serialize()\n    {\n        return serialize(array(\n            $this->id,\n            $this->nombreusuario,\n            $this->password,\n            $this->isActive\n        ));\n    }\n    \n    /** @see \\Serializable::unserialize() */\n    public function unserialize($serialized)\n    {\n        list (\n            $this->id,\n            $this->nombreusuario,\n            $this->password,\n            $this->isActive\n        ) = unserialize($serialized);\n    }\n    \n     public function isAccountNonExpired()\n    {\n        return true;\n    }\n\n    public function isAccountNonLocked()\n    {\n        return true;\n    }\n\n    public function isCredentialsNonExpired()\n    {\n        return true;\n    }\n\n    public function isEnabled()\n    {\n        return $this->isActive;\n    }\n    \n    /**\n     * Get nombrecompleto\n     *\n     * @return string\n     */\n    public function getNombrecompleto(){\n        return $this->nombre . \" \" . $this->apellido;\n    }\n    \n    \n}\n","undoManager":{"mark":-1,"position":-1,"stack":[]},"ace":{"folds":[],"scrolltop":3000,"scrollleft":0,"selection":{"start":{"row":198,"column":22},"end":{"row":198,"column":22},