{"changed":true,"filter":false,"title":"TipoProductoType.php","tooltip":"/src/CYA/YogaBundle/Form/TipoProductoType.php","value":"<?php\n\nnamespace CYA\\YogaBundle\\Form;\n\nuse Symfony\\Component\\Form\\AbstractType;\nuse Symfony\\Component\\Form\\FormBuilderInterface;\nuse Symfony\\Component\\OptionsResolver\\OptionsResolver;\nuse Symfony\\Component\\Form\\Extension\\Core\\Type\\SubmitType;\nuse Symfony\\Component\\Form\\Extension\\Core\\Type\\TextType;\nuse Symfony\\Component\\Form\\Extension\\Core\\Type\\CheckboxType;\nuse Symfony\\Component\\Form\\Extension\\Core\\Type\\ChoiceType;\n\nclass TipoproductoType extends AbstractType\n{\n    /**\n     * @param FormBuilderInterface $builder\n     * @param array $options\n     */\n    public function buildForm(FormBuilderInterface $builder, array $options)\n    {\n        $builder\n            ->add('nombre',TextType::class)\n            ->add('tipo', ChoiceType::class, array('choices' => array('Credito' => 'C', 'Debito' => 'D'), 'placeholder' => 'Seleccione un tipo'))\n            ->add('isActive', CheckboxType::class, array('required' => false,'data' => true))\n            ->add('save', SubmitType::class)  \n        ;\n    }\n    \n    /**\n     * @param OptionsResolver $resolver\n     */\n    public function configureOptions(OptionsResolver $resolver)\n    {\n        $resolver->setDefaults(array(\n            'data_class' => 'CYA\\YogaBundle\\Entity\\Rubro'\n        ));\n    }\n\n    /**\n     * {@inheritdoc}\n     */\n    public function getBlockPrefix()\n    {\n        return 'cya_yogabundle_rubro';\n    }\n\n\n}\n","undoManager":{"mark":-2,"position":4,"stack":[[{"start":{"row":12,"column":6},"end":{"row":12,"column":11},"action":"remove","lines":["Rubro"],"id":2},{"start":{"row":12,"column":6},"end":{"row":12,"column":7},"action":"insert","lines":["T"]}],[{"start":{"row":12,"column":7},"end":{"row":12,"column":8},"action":"insert","lines":["i"],"id":3}],[{"start":{"row":12,"column":8},"end":{"row":12,"column":9}